/** @format */

import React from 'react';
import { connect } from 'react-redux';

import './sales-table.style.scss';

class SalesTable extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			id: '',
			isActive: false,
			newQuantity: '',
		};
	}
	handleClick = (e) => {
		console.log(e.target);
		this.setState({
			id: e.target.id,
			isActive: !this.state.isActive,
		});
	};

	render() {
		console.log(this.state);
		return (
			<div className='sales-table-container'>
				<div className='sales-table'>
					<div className='heading-row'>
						<div className='cell'>
							<span>S. No:</span>
						</div>
						<div className='cell'>
							<span>Name</span>
						</div>
						<div className='cell'>
							<span>price</span>
						</div>
						<div className='cell'>
							<span>Quantity</span>
						</div>
						<div className='cell'>
							<span>Total</span>
						</div>
						<div className='cell'>
							<span>Action</span>
						</div>
					</div>
					{this.props.oderList.map((item, indx) => {
						return (
							<div key={indx + 1} className='data-row'>
								<form>
									<div className='cell'>
										<span>{item.id}</span>
									</div>
									<div className='cell'>
										<span>{item.name}</span>
									</div>
									<div className='cell'>
										<span>{item.price}</span>
									</div>
									<div onClick={this.handleClick} className='cell'>
										<input
											id={indx}
											type='text'
											placeholder='Quantity'
											value={
												!this.state.id ? item.quantity : this.state.newQuantity
											}
											disabled={this.state.id === indx ? true : false}
										/>
									</div>
									<div className='cell'>
										<span>{item.quantity * item.price}</span>
									</div>
									<div className='cell'>
										<span>Edit | Dele</span>
									</div>
								</form>
							</div>
						);
					})}
				</div>
			</div>
		);
	}
}

const mapStateToProp = (state) => ({
	oderList: state.oder.oderList,
});

export default connect(mapStateToProp, null)(SalesTable);
