/** @format */

import React from 'react';
import SearchInput from '../search-inut-box/search-input.box';

import './sale-form.style.scss';

class SaleForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			cursor: 0,
			productId: '',
			productName: '',
			productPrice: '',
			listForId: [],
			listForName: [],
			list: [
				{ id: 1, name: 'milk', price: 2 },
				{ id: 2, name: 'water', price: 1 },
				{ id: 3, name: 'tea', price: 3 },
			],
		};
	}

	handleChange = (e) => {
		const { name, value, id } = e.target;
		const FilterData = () => {
			switch (name) {
				case 'productId':
					return this.state.list.filter((data) => {
						return Number(data.id) === Number(value);
					});

				case 'productName':
					return this.state.list.filter((data) => {
						return data.name.includes(value);
					});

				default:
					return this.state.list;
			}
		};
		const newList = FilterData();
		if (id === 'id') {
			return this.setState({ [name]: value, listForId: newList });
		}
		if (id === 'name') {
			return this.setState({ [name]: value, listForName: newList });
		}
	};

	handleKeyDown = (e) => {
		if (e.keyCode === 40 || e.keyCode === 38 || e.keyCode === 13) {
			e.preventDefault();
			console.log(e.keyCode);
			if (
				(e.keyCode === 40 &&
					this.state.cursor < this.state.listForId.length - 1) ||
				this.state.listForName.length - 1
			) {
				return this.setState((prevState) => ({
					...prevState,
					cursor: prevState.cursor + 1,
				}));
			}
			if (e.keyCode === 38 && this.state.cursor > 0) {
				return this.setState((prevState) => ({
					...prevState,
					cursor: prevState.cursor - 1,
				}));
			}
			if (
				(e.keyCode === 13 && this.state.listForId.length) ||
				this.state.listForName.length
			) {
				return this.setState({
					productId:
						this.state.this.state.listForId.length -
							(1)[this.state.cursor].id ||
						this.state.this.state.listForName.length -
							(1)[this.state.cursor].id,
					productName:
						this.state.displayForId[this.state.cursor].name ||
						this.state.displayForName[this.state.cursor].name,
					productPrice:
						this.state.displayForId[this.state.cursor].price ||
						this.state.displayForName[this.state.cursor].name,
					displayList: [],
				});
			}
		}
		return;
	};
	render() {
		console.log(this.state);
		return (
			<form className='sale-panel'>
				<SearchInput
					id='id'
					name='productId'
					placeholder='Product Id'
					value={this.state.productId}
					cursor={this.state.cursor}
					keyDown={this.handleKeyDown}
					list={this.state.listForId.map((data) => data.id)}
					autoComplete='off'
					handleChange={this.handleChange}
				/>
				<SearchInput
					id='name'
					type='text'
					name='productName'
					placeholder='Product Name'
					value={this.state.productName}
					cursor={this.state.cursor}
					list={this.state.listForName.map((data) => data.name)}
					handleChange={this.handleChange}
					keyDown={this.handleKeyDown}
					autoComplete='off'
				/>
				<p>Price: {0}</p>
				<p>Value: {0}</p>

				<button>Submit</button>
			</form>
		);
	}
}

export default SaleForm;
