/** @format */

import React from 'react';

import './table.row.scss';

class TableRow extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			id: '',
			quantity: this.props.item.quantity,
			isActive: false,
		};
	}
	onButtonPress = (e) => {
		e.preventDefault();
		if (e.keyCode === 13) {
			this.setState({ ...this.state, isActive: false });
		}
	};
	handleClick = (e) => {
		this.setState((prevState) => {
			if (this.state.quantity && e.keyCode !== 13) {
				return {
					...prevState,
					isActive: !prevState.isActive,
				};
			}
			return {
				...prevState,
				isActive: !prevState.isActive,
				quantity: this.props.item.quantity,
			};
		});
	};
	handleChange = (e) => {
		console.log(e.target);
		e.preventDefault();
		const { name, value } = e.target;
		this.setState({ [name]: value });
	};
	render() {
		console.log(this.state.quantity === '', '===----', this.props.quantity);
		return (
			<div key={this.props.indx} className='data-row'>
				<form>
					<div className='cell'>
						<span>{this.props.item.id}</span>
					</div>
					<div className='cell'>
						<span>{this.props.item.name}</span>
					</div>
					<div className='cell'>
						<span>{this.props.item.price}</span>
					</div>
					<div className='cell'>
						<input
							className={this.state.isActive ? 'inActive' : null}
							id={this.props.indx}
							type='text'
							name='quantity'
							placeholder='Quantity'
							value={this.state.quantity}
							disabled={this.state.isActive ? false : true}
							onChange={this.handleChange}
							onKeyDown={this.onButtonPress}
						/>
					</div>
					<div className='cell'>
						<span>{this.props.item.quantity * this.props.item.price}</span>
					</div>
					<div className='cell'>
						<span onClick={this.handleClick}>Edit | Dele</span>
					</div>
				</form>
			</div>
		);
	}
}
export default TableRow;
