/** @format */

const oderList = (state, item) => {
	// const newitem = { id: 3, name: 'tea', price: 3, quantity: 5 };
	// const list = [
	// 	{ id: 1, name: 'milk', price: 2, quantity: 30 },
	// 	{ id: 2, name: 'water', price: 1, quantity: 10 },
	// 	{ id: 3, name: 'tea', price: 3, quantity: 5 },
	// ];
	console.log(oderList);
	console.log(item);
	const isInclude = state.oderList.find((data) => data.id === item.id);

	if (!!isInclude) {
		return [...oderList];
	}

	return [...oderList, item];
};

export default oderList;
