import React from 'react';
import { connect } from 'react-redux';

import './infobar.style.scss';

const InfoBar = () => {
	return <p>Message</p>;
};

const mapStateToProp = (state) => ({
	message: state.oder.message,
});
export default connect(mapStateToProp, null)(InfoBar);
