/** @format */

const oderList = (oderList, item) => {
	const newitem = { id: 3, name: 'tea', price: 3, quantity: 5 };
	const list = [
		{ id: 1, name: 'milk', price: 2, quantity: 30 },
		{ id: 2, name: 'water', price: 1, quantity: 10 },
		{ id: 3, name: 'tea', price: 3, quantity: 5 },
	];

	const isInclude = list.find((item) => item.id === newitem.id);

	if (!!isInclude) {
		console.log(!!isInclude);
	}
};

export default oderList;
